package Chap04;

public class Example08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;//정수 변수 i 선언, 값 1 할당
		
		//i가 10보다 작을때 무한으로 돌도록 while문 생성
		while (i <= 10) {
			System.out.print(i + " ");//i의 값을 출력
			i++;//i는 이후부터 증가.
			
			//i의 값이 10보다 클때까지 다시 while문의 처음 부터 동작하도록 도돌임표
		}
	}

}
