package chap03;

public class Example05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		System.out.println(x++);//x를 출력 하는데 ++는 아직 하지마, 다음줇 부터 적용해
		System.out.println(++x);//x를 출력 하는데 ++하고 출력해
		System.out.println(x--);//x를 출력 하는데 --는 아직 하지마, 다음줄 부터 적용해
		System.out.println(--x);//x를 출력 하는데 --하고 출력해
	}

}
