package chap03;

public class Operator02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 25;
		System.out.println("a == b = " + (a == b));//a는b인가?
		System.out.println("a != b = " + (a != b));//a는 b가 아닌가?
		System.out.println("a > b = " + (a > b));//a는 b보다 큰가?
		System.out.println("a < b = " + (a < b));//a는 b보다 작은가?
		System.out.println("b >= a = " + (b >= a));//b는 a보다 크거나 같은가?
		System.out.println("b <= a = " + (b <= a));//b는 a보다 작거나 같은가?
	}

}
